//Function to update the visitor count displayed on the webpage
function updateVisitorCount(count) {
  const visitorCountElement = document.getElementById('visitor-count');
  if (visitorCountElement) {
    visitorCountElement.textContent = count;
    console.log('Updated visitor count:', count);
  } else {
    console.error('Element with ID "visitor-count" not found');
  }
}

// Event listener to increment the count when the page loads
window.addEventListener('load', async () => {
  try {
    const response = await fetch('https://4wsr0mg4qi.execute-api.us-east-1.amazonaws.com/production/count', {
      method: 'POST',
    });

    if (!response.ok) {
      throw new Error('Failed to increment visitor count');
    }

    const data = await response.json();
    const count = JSON.parse(data.body).count;
    console.log('Incremented visitor count:', count);
    updateVisitorCount(count);
  } catch (error) {
    console.error('Error incrementing visitor count:', error);
    updateVisitorCount('Error');
  }
});